sqlMap.select:
SELECT B.PD_NAME PD_ID, B.PD_ALIAS PD_NAME FROM (SELECT PD_ID, PD_NAME, PD_ALIAS, CASE WHEN SIGN(((SYSDATE - FINAL_MO_REG_DATE) * 24 * 60 * 60 * 1000) - (3 * 60 * 1000)) != '-1' THEN 'ERROR' WHEN SIGN(((SYSDATE - FINAL_MO_REG_DATE) * 24 * 60 * 60 * 1000) - (3 * 60 * 1000)) IS NULL THEN 'ERROR' ELSE 'SUCCESS' END AS PD_STATUS FROM ESB_PRODUCT A WHERE A.PD_TYPE=#PD_TYPE#
PD_GROUP_ID = #GROUP_ID#
) B WHERE PD_STATUS = #STATUS#

SELECT B.PD_NAME PD_ID, B.PD_ALIAS PD_NAME FROM (SELECT PD_ID, PD_NAME, PD_ALIAS, CASE WHEN STATUS IN ('1','0') THEN 'SUCCESS' WHEN STATUS = 'NULL' THEN 'ERROR' ELSE 'ERROR' END AS AD_STATUS FROM ( SELECT PD_ID, PD_NAME, PD_ALIAS, DECODE(SIGN(((sysdate - FINAL_MO_REG_DATE) * 24 * 60 * 60 * 1000) - (3 * 60 * 1000)),1,'-9', DECODE(FINAL_MO_STATUS,'-2','-2','-1','-1', DECODE(FINAL_NW_STATUS,'99','2',nvl(FINAL_MO_STATUS,'NULL')))) AS STATUS FROM ESB_PRODUCT WHERE PD_TYPE=#PD_TYPE#
PD_GROUP_ID = #GROUP_ID#
)) B WHERE AD_STATUS=#STATUS#
