sqlMap.select:
select count(*) from (select CH_ID, CHARGER_NAME, CHARGER_DEPTH, CHARGER_GROUP, CHARGER_TELNUM, CHARGER_CELLNUM, GUBUN_USER, GUBUN_LIVE, REG_DATE, IM_ID from esb_IMCHARGER
where CHARGER_NAME LIKE '%' || #CH_NAME# || '%'
)t1

SELECT count(*) FROM (select z.* from (select CH_ID, CHARGER_NAME, CHARGER_DEPTH, CHARGER_GROUP, CHARGER_TELNUM, CHARGER_CELLNUM, GUBUN_USER, GUBUN_LIVE, REG_DATE, IM_ID from esb_IMCHARGER
where IM_ID LIKE '%' || #IM_ID# || '%'
)z )t

SELECT t.* FROM (select z.* from (select CH_ID, CHARGER_NAME, CHARGER_DEPTH, CHARGER_GROUP, CHARGER_TELNUM, CHARGER_CELLNUM, GUBUN_USER, GUBUN_LIVE, REG_DATE, IM_ID from esb_IMCHARGER where IM_ID LIKE '%' || #PD_ID# || '%'
)z)t
OFFSET #SKIP_INDEX# ROWS FETCH first #MAX_ROW# ROW ONLY
