sqlMap.select:
SELECT DEPLOYID , MAX(case when LS.STATE = 'N' then CNT else 0 end) AS STATEN , MAX(case when LS.STATE = 'P' then CNT else 0 end) AS STATEP , MAX(case when LS.STATE = 'T' then CNT else 0 end) AS STATET , MAX(case when LS.STATE = 'S' then CNT else 0 end) AS STATES , MAX(case when LS.STATE = 'F' then CNT else 0 end) AS STATEF FROM (SELECT STATE, DEPLOYID, COUNT(*) CNT FROM LIBBATCHUPDATE_STATUS GROUP BY STATE, DEPLOYID) LS WHERE DEPLOYID = (SELECT LD.DEPLOYID FROM LIBBATCHUPDATE_ZIP LZ, LIBBATCHUPDATE_DEPLOY LD WHERE LZ.ZIPID = LD.ZIPID
LZ.ZIPID = #ZIPID#
) GROUP BY DEPLOYID

SELECT IM_NAME FROM LIBBATCHUPDATE_STATUS WHERE DEPLOYID = (SELECT LD.DEPLOYID FROM LIBBATCHUPDATE_ZIP LZ, LIBBATCHUPDATE_DEPLOY LD WHERE LZ.ZIPID = LD.ZIPID
LZ.ZIPID = #ZIPID#
)
